	TB_MASTER_GROUP_MANAGE(
		master_idx,
		group_idx
	) VALUES (
		#{idx},
		#{groupIdx}
	)

mapper.update:
    UPDATE
		TB_MASTER
	SET
		master_id = #{masterId}

			, master_pw = #{masterPw}


